<template>
  <div :class="[$style.wrap, activeLeftPanelListItemInd === 2 && 'block']">
    <MyButton :class="$style.btn" v-if="[0,1].includes(activeLeftPanelListItemInd)">
      <img src='data:image/svg+xml;base64,' alt=''/>
      <div :class="$style.colorSubmenu">
        <SVGGenerator name="circle"
                      @updateBackgroundColorHandler="updateBackgroundColor"
        />
      </div>
    </MyButton>

    <MyButton :class="$style.btn" v-if="[0,1].includes(activeLeftPanelListItemInd) && !inTopPanel"
              title="Выбрать картинку"
    >
      <img src='data:image/svg+xml;base64,' alt=''/>
      <label :class="$style.choosePicBtn" @change="$emit('choosePic', $event)" >
        <input type="file" :multiple="true" :hidden="true"/>
      </label>
    </MyButton>

    <MyButton title='Архивировать'
              @click="$emit('addToArchive')" v-if="[0].includes(activeLeftPanelListItemInd)"
    >
      <img src='data:image/svg+xml;base64,' alt=''/>
    </MyButton>

    <MyButton title='Разархивировать' @click="$emit('returnFromArchive')" v-if="[1].includes(activeLeftPanelListItemInd) ">
      <img src='data:image/svg+xml;base64,' alt=''/>
    </MyButton>

    <MyButton title='Удалить навсегда' @click="$emit('deleteForever')" v-if="[2].includes(activeLeftPanelListItemInd) ">
      <img src='data:image/svg+xml;base64,' alt=''/>
    </MyButton>
    <MyButton title='Вернуть из корзины' @click="$emit('returnFromTrash')" v-if="[2].includes(activeLeftPanelListItemInd) ">
      <img src='data:image/svg+xml;base64,' alt=''/>
    </MyButton>

    <MyButton :class="$style.btn" title="Еще" @click="$emit('deleteNote')"
              v-if="[0,1].includes(activeLeftPanelListItemInd) && !inAddForm "
    >
      <img width="24" height="24" alt="" src="data:image/png;base64,"/>
      <div :class="$style.moreSubmenu">Удалить</div>
    </MyButton>
  </div>

</template>

<script>
export default {
  name: "MyMenu",
  inject: ['activeLeftPanelListItemInd'],
  props: {
    modelBackgroundColor: String,
    inAddForm: Boolean,
    inTopPanel: Boolean,
    noteId: Number,
    changeNoteBackgroundColor: Function
  },
  data() {
    return {
      activeLeftPanelListItemInd: this.activeLeftPanelListItemInd
    }
  },
  methods: {
    updateBackgroundColor(color) {
      if (this.inAddForm) {
        this.$emit('update:backgroundColor', color)
      }
      else {
        this.changeNoteBackgroundColor(this.noteId, color)
      }
    }
  }
}
</script>

<style module>
.wrap {
  display: flex;
  justify-content: space-between;
}
.btn {
  position: relative;
  width: 24px;
  height: 24px;
}
.colorSubmenu, .moreSubmenu {
  position: absolute;
  border: 1px solid black;
  display: none;
  background: white;
  z-index: 2;
}
.btn:hover .colorSubmenu, .btn:hover .moreSubmenu  {
  display: block;
}
.moreSubmenu{
  display: none;
}
.choosePicBtn {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  cursor: pointer;
}
.block{
  display: block;
}

</style>